package com.example.digicu_customer.ui.main.search;

import android.util.Log;

import androidx.lifecycle.MutableLiveData;
import androidx.lifecycle.ViewModel;

import com.example.digicu_customer.data.dataset.ShopDataModel;
import com.example.digicu_customer.data.remote.ApiUtils;
import com.example.digicu_customer.data.remote.DigicuService;
import com.example.digicu_customer.general.GeneralVariable;

import java.util.ArrayList;
import java.util.List;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;

public class SearchViewModel extends ViewModel {
    MutableLiveData<List<ShopDataModel>> shopDataModelMutableLiveData;

    public MutableLiveData<List<ShopDataModel>> getShopDataModelMutableLiveData() {
        if (shopDataModelMutableLiveData == null) {
            shopDataModelMutableLiveData = new MutableLiveData<>();
            loadShopDataModel();
        }

        return shopDataModelMutableLiveData;
    }

    private void loadShopDataModel() {
        DigicuService digicuService = ApiUtils.getDigicuService();

        digicuService.getCompanyInfo("rlsmd9@naver.com").enqueue(new Callback<ShopDataModel>() {
            @Override
            public void onResponse(Call<ShopDataModel> call, Response<ShopDataModel> response) {
                List<ShopDataModel> shopDataModels = shopDataModelMutableLiveData.getValue();
                if (shopDataModels == null) {
                    shopDataModels = new ArrayList<>();
                }

                shopDataModels.add(response.body());

                // for signaling to observer
                shopDataModelMutableLiveData.setValue(shopDataModels);
            }

            @Override
            public void onFailure(Call<ShopDataModel> call, Throwable t) {
                Log.d(GeneralVariable.TAG, "onFailure header: " + t.toString());
            }
        });

//        shopDataModels.add(new ShopDataModel("test1", "상도동1", "010-1234-1234", "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f1/Samsung_logo_blue.png/1200px-Samsung_logo_blue.png"));
//        shopDataModels.add(new ShopDataModel("test2", "상도동2", "010-1234-1234", "data:image/png;base64,"));
//        shopDataModels.add(new ShopDataModel("test3", "상도동3", "010-1234-1234", "https://i.pinimg.com/originals/d5/af/73/d5af7344a7415abe0908fd01e28adc54.png"));
//        shopDataModels.add(new ShopDataModel("test4", "상도동4", "010-1234-1234", "https://image.freepik.com/free-vector/coffee-cafe-logo-design_57043-146.jpg"));
//        shopDataModels.add(new ShopDataModel("test5", "상도동5", "010-1234-1234", "https://www.pngkey.com/png/detail/192-1929751_colombian-restaurant-colombian-cafe-coffee-barista-cafe-logo.png"));

    }
}
